option.value:
// Centralise les montants de récompenses pour chaque action monstre
// Modifiez les valeurs ici sans toucher à la logique domaine
export const REWARD_AMOUNTS = {
  feed: 2,
  cuddle: 1,
  play: 2,
  clean: 1
} as const

export type RewardActionKey = keyof typeof REWARD_AMOUNTS

// Optionnel: export d'une liste typée (utile pour des UIs ou validations)
export const AVAILABLE_REWARD_ACTIONS: readonly RewardActionKey[] =
  Object.keys(REWARD_AMOUNTS) as readonly RewardActionKey[]

export type MonsterActionType = 'feed' | 'cuddle' | 'play' | 'clean'

const ACTION_REWARDS: Record<MonsterActionType, number> = {
    feed: 2,
    cuddle: 1,
    play: 2,
    clean: 1
}

/**
 * Retourne la récompense en koins pour une action.
 */
export function getRewardForAction (action: MonsterActionType): number {
    return ACTION_REWARDS[action] ?? 0
}

import { REWARD_AMOUNTS } from '@/config/rewards'

export type MonsterActionType = keyof typeof REWARD_AMOUNTS

/**
 * Retourne la récompense en koins pour une action.
 */
export function getRewardForAction (action: MonsterActionType): number {
    return REWARD_AMOUNTS[action] ?? 0
}

export const AVAILABLE_ACTIONS: readonly MonsterActionType[] =
  Object.keys(REWARD_AMOUNTS) as readonly MonsterActionType[]
